--
--  "initialize" code for Mission08 HonourGuardSwarm FSM
--
function Init_Mission08_HonourGuardSwarm(TeamName)
	--FSM: FSM_Mission08_HonourGuardSwarm
	--TeamName = "HighGuardSwarmers1"
	DockCount[TeamName] = 0 --created,set
	PodCount[TeamName] = 1 --created,set
	SobGroup_SetROE(TeamName, PassiveROE)
	if (KAS_TeamNamesEqual(TeamName, "HighGuardSwarmers1") ~= 0) then     --kasfThisTeamIs(kasAITeamPtr("HighGuardSwarmers1"))
	
	--kasJump("WaitingLeader")
	KASRule_Add(TeamName, "Watch_Mission08_HonourGuardSwarm_WaitingLeader", "Init_Mission08_HonourGuardSwarm")
	Init_Mission08_HonourGuardSwarm_WaitingLeader(TeamName)
	return
	elseif (KAS_TeamNamesEqual(TeamName, "HighGuardSwarmers2") ~= 0) then     --kasfThisTeamIs(kasAITeamPtr("HighGuardSwarmers2"))
	
	--kasJump("Waiting")
	KASRule_Add(TeamName, "Watch_Mission08_HonourGuardSwarm_WaitingLeader", "Init_Mission08_HonourGuardSwarm")
	Init_Mission08_HonourGuardSwarm_WaitingLeader(TeamName)
	return
	end 
	
end 


