--
--  "watch" code for Mission08 Mothership FSM
--
function Watch_Mission08_Mothership(TeamName)
	--FSM: FSM_Mission08_Mothership
	--TeamName = "MSTeam1"
	if (_VIFONCE017[TeamName] == 0 and (SobGroup_Count(TeamName) == 0)) then  -- "ifonce" #17    --kasfTeamCount()
	
	_VIFONCE017[TeamName] = 1 --created,set;
	ImDead[TeamName] = 1 --created,set
	--kasJump("NullState")
	--KASRule_Remove("FSM_Mission08_Mothership", "Watch_Mission08_Mothership")
	KASRule_Add(TeamName, "Watch_Mission08_Mothership_NullState", "Watch_Mission08_Mothership")
	Init_Mission08_Mothership_NullState(TeamName)
	return
	end 
	if (ImDead[TeamName] == 0) then 
	
	if (_VIFONCE018[TeamName] == 0 and (KAS_UnderAttack(TeamName, "GrowSelection_Poopyheads") ~= 0)) then  -- "ifonce" #18    --kasfUnderAttack(kasGrowSelectionPtr("Poopyheads"))
	
	_VIFONCE018[TeamName] = 1 --created,set;
	--kasJump("DefendSelf")
	--KASRule_Remove("FSM_Mission08_Mothership", "Watch_Mission08_Mothership")
	KASRule_Add(TeamName, "Watch_Mission08_Mothership_DefendSelf", "Watch_Mission08_Mothership")
	Init_Mission08_Mothership_DefendSelf(TeamName)
	return
	end 
	if (_VIFONCE019[TeamName] == 0 and (KAS_TeamHealthLowest(TeamName) < 30)) then  -- "ifonce" #19    --kasfTeamHealthLowest()
	
	_VIFONCE019[TeamName] = 1 --created,set;
	ImHurt[TeamName] = 1 --created,set
	G_MothershipsHurt = G_MothershipsHurt + 1
	--kasJump("DefendSelf")
	--KASRule_Remove("FSM_Mission08_Mothership", "Watch_Mission08_Mothership")
	KASRule_Add(TeamName, "Watch_Mission08_Mothership_DefendSelf", "Watch_Mission08_Mothership")
	Init_Mission08_Mothership_DefendSelf(TeamName)
	return
	end 
	DeadOrAttacking[TeamName] = (G_MothershipsAttacking + G_MothershipsDead) --created,set
	if (_VIFONCE020[TeamName] == 0 and ((DeadOrAttacking[TeamName] == 2) or ((ImHurt[TeamName] == 0) and ((DeadOrAttacking[TeamName] == 1) and (G_MothershipsHurt == 1)) or (G_MothershipsHurt == 2)))) then  -- "ifonce" #20
	
	_VIFONCE020[TeamName] = 1 --created,set;
	GettingOutOfHere[TeamName] = 1 --created,set
	KAS_MessageSend(TeamName, TeamName, "Wave")    --kasfMsgSend(kasThisTeamPtr, "Wave")
	end 
	end 
	
end 


