function Watch_Mission08(MissionName)
	--FSM: FSM_Mission08
	--MissionName = "Mission08"
	KAS_SensorsWeirdness((350 * Universe_GetWorldResources())/WorldStartRUs)    --kasfGetWorldResources()    --kasfSensorsWeirdness((350 * Universe_GetWorldResources()) / ___KAS_SCOPED_VAR____WorldStartRUs_)
	if (_VIFONCE021 == 0 and (KAS_PlayerHasResearch(0, "CorvetteDrive") ~= 0)) then  -- "ifonce" #21    --kasfDoesPlayerHave("MassDrive10Kt")
	
	_VIFONCE021 = 1 --created,set;
	KAS_UnrestrictResearchOption(0, "CorvetteChassis")    --kasfAllowPlayerToResearch("Chassis2")
	end 
	if ((KAS_PlayerHasResearch(0, "CorvetteChassis") ~= 0) and (SetMediumGuns == 0)) then     --kasfDoesPlayerHave("Chassis2")
	
	KAS_UnrestrictResearchOption(0, "HeavyCorvetteUpgrade")    --kasfAllowPlayerToResearch("MediumGuns")
	SetMediumGuns = 1 --created,set
	end 
	if (_VIFONCE022 == 0 and (KAS_PlayerHasResearch(0, "FighterChassis") ~= 0)) then  -- "ifonce" #22    --kasfDoesPlayerHave("Chassis1")
	
	_VIFONCE022 = 1 --created,set;
	KAS_UnrestrictResearchOption(0, "CorvetteDrive")    --kasfAllowPlayerToResearch("MassDrive10Kt")
	end 
	
end 

